import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import SwipeableViews from 'react-swipeable-views';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Avatar from '@material-ui/core/Avatar';

function TabContainer({ children, dir }) {
  return (
    <Typography component="div" dir={dir} style={{ padding: 8 * 3 }}>
      {children}
    </Typography>
  );
}

TabContainer.propTypes = {
  children: PropTypes.node.isRequired,
  dir: PropTypes.string.isRequired,
};

const styles = theme => ({
  root: {
    backgroundColor: theme.palette.background.paper,
    width: '100%',
    marginTop: theme.spacing.unit * 3,
    overflowX: 'auto',
  },
  table: {
    maxWidth: 600,
  },
  row: {
    '&:nth-of-type(odd)': {
      backgroundColor: theme.palette.background.default,
    },
  },
  avatar: {
    margin: 5,
    width: 40,
    height: 40,
  },
});
const CustomTableCell = withStyles(theme => ({
  head: {
    backgroundColor: theme.palette.common.paper,
    color: theme.palette.common.black,
  },
  body: {
    fontSize: 14,
  },
}))(TableCell);

let id = 0;
function createData(photo, name, club, nation, apps, goals) {
  id += 1;
  return { id, photo, name, club, nation, apps, goals };
}

const rows = [
  createData("https://img-s-msn-com.akamaized.net/tenant/amp/entityid/BBS5ZDs.img?h=469&w=624&m=6&q=60&o=f&l=f&x=1151&y=448", <b>Eden Hazard</b>, "Chelsea", "https://upload.wikimedia.org/wikipedia/commons/thumb/6/65/Flag_of_Belgium.svg/2000px-Flag_of_Belgium.svg.png", 13, 8),
  createData('https://www.telegraph.co.uk/content/dam/football/2018/09/21/TELEMMGLPICT000172004574_trans_NvBQzQNjv4Bqe2gUmjRsyy_1k97WQihbV_HecAzVMYHuap8aHDXjqKo.jpeg?imwidth=450', 237, 9.0, 37, 4.3),
  createData('Eclair', 262, 16.0, 24, 6.0),
  createData('Cupcake', 305, 3.7, 67, 4.3),
  createData('Gingerbread', 356, 16.0, 49, 3.9),
];


class TabandTable extends React.Component {
  state = {
    value: 0,
  };

  handleChange = (event, value) => {
    this.setState({ value });
  };

  handleChangeIndex = index => {
    this.setState({ value: index });
  };

  render() {
    const { classes, theme } = this.props;

    return (
        <>
        <header>
        <Container />
        </header>
      <div className={classes.root}>
        <AppBar position="static" color="default">
          <Tabs
            value={this.state.value}
            onChange={this.handleChange}
            indicatorColor="primary"
            textColor="primary"
            variant="fullWidth"
          >
            <Tab label="Top Scorers" />
            <Tab label="Top Assistants" />
          </Tabs>
        </AppBar>
        <SwipeableViews
          axis={theme.direction === 'rtl' ? 'x-reverse' : 'x'}
          index={this.state.value}
          onChangeIndex={this.handleChangeIndex}
        >
          <TabContainer dir={theme.direction}>
      <Table className={classes.table}>
        <TableHead>
          <TableRow>
            <CustomTableCell align="center" width="190px">Rank</CustomTableCell>
            <CustomTableCell colspan="3" align="center">Player Information</CustomTableCell>
            <CustomTableCell align="center" width="190px">Apps</CustomTableCell>
            <CustomTableCell align="center" width="190px">Goals</CustomTableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map(row => (
            <TableRow className={classes.row} key={row.id}>
                          <CustomTableCell align="center">{row.id}</CustomTableCell>
              <CustomTableCell align="left"><Avatar alt="Remy Sharp" src= {row.photo} className={classes.avatar} /></CustomTableCell>
              <CustomTableCell component="th" scope="row">
                {row.name} <br/> {row.club}
              </CustomTableCell>
              <CustomTableCell align="center"> <img src= {row.nation} width="40" height="30"/></CustomTableCell>
              <CustomTableCell align="center">{row.apps}</CustomTableCell>
              <CustomTableCell align="center">{row.goals}</CustomTableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
  </TabContainer>
        <TabContainer dir={theme.direction}>
      <Table className={classes.table}>
        <TableHead>
          <TableRow>
            <CustomTableCell align="center" width="190px">Rank</CustomTableCell>
            <CustomTableCell colspan="3" align="center">Player Information</CustomTableCell>
            <CustomTableCell align="center" width="194px">Apps</CustomTableCell>
            <CustomTableCell align="center" width="194px">Assists</CustomTableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map(row => (
            <TableRow className={classes.row} key={row.id}>
              <CustomTableCell align="left">{row.id}</CustomTableCell>  
              <CustomTableCell align="left"><Avatar alt="Remy Sharp" src= {row.photo} className={classes.avatar} /></CustomTableCell>
              <CustomTableCell component="th" scope="row">
                {row.name} <br/> {row.club}
              </CustomTableCell>
              <CustomTableCell align="center"> <img src= {row.nation} width="45" height="35"/> </CustomTableCell>
              <CustomTableCell align="center">{row.apps}</CustomTableCell>
              <CustomTableCell align="center">{row.goals}</CustomTableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
  </TabContainer>
        </SwipeableViews>
      </div>
      </>
    );
  }
}

TabandTable.propTypes = {
  classes: PropTypes.object.isRequired,
  theme: PropTypes.object.isRequired,
};


export default withStyles(styles, { withTheme: true })(TabandTable);
